import React, {Component} from 'react';

export default class CalculatorItem extends Component {
    constructor() {
        super();
        this.state = {
            stateText: event.target.value
        }

        this.updateText = () => this.setState ({

        })
    }

    render() {
        return (
            <div id="calculatorMain">
                <div id="row">
                    <input type="text" id="result" readonly class="noPadding"/>
                </div>
                <div id="row1">
                    <input type="button" value="7" onclick="addNum(value)"/>
                    <input type="button" value="8" onclick="addNum(value)"/>
                    <input type="button" value="9" onclick="addNum(value)"/>
                    <input type="button" value="CLR" id="clear" onclick="clearScreen()" class="noPadding"/>
                </div>
                <div id="row2">
                    <input type="button" value="4" onclick="addNum(value)"/>
                    <input type="button" value="5" onclick="addNum(value)"/>
                    <input type="button" value="6" onclick="addNum(value)"/>
                    <input type="button" value="*" onclick="addNum(value)" class="noPadding"/>
                </div>
                <div id="row3">
                    <input type="button" value="1" onclick="addNum(value)"/>
                    <input type="button" value="2" onclick="addNum(value)"/>
                    <input type="button" value="3" onclick="addNum(value)"/>
                    <input type="button" value="-" onclick="addNum(value)" class="noPadding"/>
                </div>
                <div id="row4">
                    <input type="button" value="0" onclick="addNum(value)"/>
                    <input type="button" value="%" onclick="addNum(value)"/>
                    <input type="button" value="=" onclick="calculate()"/>
                    <input type="button" value="+" onclick="addNum(value)" class="noPadding"/>
                </div>
            </div>
        );
    }
}
